mapper.select:
SELECT t_users.userID, t_users.userName, t_users.birthday, t_users.address, t_users.phoneNumber, t_users.state, string_agg(t_role.role,'/'ORDER BY t_role.userID) AS role FROM t_users INNER JOIN t_role ON t_users.userID=t_role.userID
AND role = #{role}
AND t_users.userID = #{userID}
AND userName = #{userName}
AND birthday = #{birthday}
AND address = #{address}
AND phoneNumber = #{phoneNumber}
AND state = #{state}
AND state = 'ACTV'
GROUP BY t_users.userID ORDER BY t_users.userID

SELECT count(*) FROM t_users
INNER JOIN t_role ON t_users.userID = t_role.userID
AND t_users.userID = #{userID}
AND userName = #{userName}
AND birthday = #{birthday}
AND address = #{address}
AND phoneNumber = #{phoneNumber}
AND state = #{state}
AND role = #{role}
AND state = 'ACTV'
